import { StatusBar } from "expo-status-bar";
import React, { useEffect, useState } from "react";
import { StyleSheet, View } from "react-native";
import CurrentWeather from "./components/CurrentWeather";
// import * as Location from "expo-location";
const API_KEY = "f70a0961daee4a52ea707c0000e6be44";

export default function App() {
  const [data, setData] = useState({});
  useEffect(() => {
    navigator.geolocation.getCurrentPosition(
      (success) => {
        let { latitude, longitude } = success.coords;
        fetchDataFromApi("31.43301", "-100.49091");
      },
      (err) => {
        if (err) {
          fetchDataFromApi("31.43301", "-100.49091");
        }
      }
    );
  }, []);

  const fetchDataFromApi = (latitude, longitude) => {
    fetch(
      `https://api.openweathermap.org/data/2.5/onecall?lat=${latitude}&lon=${longitude}&exclude=hourly,minutely&units=metric&appid=fde9f65a3fa7a456dacbde8a2369bb35`
    )
      .then((res) => res.json())
      .then((data) => {
        console.log(data.current);
        setData(data.current);
      });
  };

  return (
    <View style={styles.container}>
      <CurrentWeather current={data.current} />
      <StatusBar style="auto" />
    </View>
  );

  const styles = StyleSheet.create({
    container: {
      display: "flex",
      flex: 1,
      backgroundColor: "#fff",
      alignItems: "center",
      justifyContent: "center",
    },
  });
}
